query {
getAdminDraftListings(
  perPage: 5,
  page: 1,
  sortColumn: BedRoom,
  sortDirection: Desc

){
  	items{
      id
      squareFeet
      owner {
        name
        phoneNumber
      }
      address {
        area
      }
      adminPhotographer {
        name
        email
      }
      adminProcessor {
        email
        name
      }
      bathroom
      bedroom
      scheduledPhotographyDateTime
      scheduledPhotographyDuration
      updatedAt
		}
    metadata{
      hasNextPage
      hasPreviousPage
      itemCount
      pageCount
      perPage
    }
  }
}

