graphql.query:
query {
getAdminDraftListings(
  sortColumn: Photographer,
  sortDirection: Asc,
  perPage: 30,
  page: 100,

){
  	items{    
      id
      unitType
      area
      squareFeet
      adminPhotographer{
          id
          name
          phone
          role
          email
          status
          isVerified
          password
          passwordResetToken
          verificationToken
          createdAt
          updatedAt
      }
      createdAt
      updatedAt
      listingId
      property {
        location {
          name
        }
      }
      owner {
        name
        role
        phoneNumber
      }

      id

		}

    metadata{
      hasNextPage
      hasPreviousPage
      itemCount
      pageCount
      perPage
    }

  }
}

query {
getAdminDraftListings(
  sortColumn: Photographer,
