query {
getAdminDraftListings(
  sortColumn: Processor,
  sortDirection: Desc,
  perPage: 30,
  page: 10,

){
  	items{    
      id
      unitType
      area
      squareFeet
      adminProcessor{
          id
          name
          phone
          role
          email
          status
          isVerified
          password
          passwordResetToken
          verificationToken
          createdAt
          updatedAt
      }
      createdAt
      updatedAt
      listingId
      property {
        location {
          name
        }
      }
      owner {
        name
        role
        phoneNumber
      }

      id

		}

    metadata{
      hasNextPage
      hasPreviousPage
      itemCount
      pageCount
      perPage
    }

  }
}

